import React from 'react'

export default function ExpenseTracker(){

    // const [item, setitem] = useState();
    var item = '';
    var amount = 0;
    var totalitem = [];
    const addItemtoCard = ( ) => {
        totalitem.push(item)
        console.log(item,totalitem)
    }

    return(
        <>
            <div className="container mt-5">
                <div className="card p-3" >
                    <div className="card-title text-center">
                        <h2>Expense Tracker</h2>
                    </div>
                    <div className="card-body">
                        Item Name:
                        <input className="form-control form-control-sm" type="text" placeholder="Enter Item Name" aria-label=".form-control-sm example" value={item}/>
                        <br/>
                        Item Amount:
                        <input className="form-control form-control-sm" type="text" placeholder="Enter Item Amount" aria-label=".form-control-sm example" value={amount}/>
                        <br/>
                        <div className="d-flex justify-content-center align-center">
                            <button type="button" className="btn btn-outline-success" onClick={addItemtoCard}>ADD Item</button>&nbsp;
                            <button type="button" className="btn btn-outline-danger">Clear ALL</button>&nbsp;
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}
